import React, { useState, useEffect } from 'react';
import './App.css';
import SearchBar from './components/SearchBar';
import PostList from './components/PostList';

function App() {
  console.log("App component rendering");

  useEffect(() => {
    console.log("App component mounted");
    // Check if backend is reachable
    fetch('http://localhost:9000/api/search?keyword=test')
      .then(response => console.log('Backend response:', response.status))
      .catch(err => console.error('Backend error:', err));
  }, []);

  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searched, setSearched] = useState(false);

  const searchReddit = async (keyword) => {
    console.log("Searching for:", keyword);
    setLoading(true);
    setError(null);
    setSearched(true);

    try {
      const response = await fetch(`http://localhost:9000/api/search?keyword=${encodeURIComponent(keyword)}`);
      console.log("Search response:", response);

      if (!response.ok) {
        throw new Error('Failed to fetch data from Reddit');
      }

      const data = await response.json();
      console.log("Received data:", data);
      setPosts(data.posts);
    } catch (err) {
      console.error("Search error:", err);
      setError(err.message);
      setPosts([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <div className="container">
          <h1>Reddit Social Listening</h1>
          <p>Monitor Reddit posts for keywords of interest</p>
        </div>
      </header>

      <main className="container">
        <div style={{padding: '20px', background: '#f0f0f0', borderRadius: '5px', marginTop: '20px'}}>
          <h2>Debug Section</h2>
          <p>If you can see this, React is rendering correctly.</p>
          <button onClick={() => searchReddit('test')}>Test API Call</button>
        </div>

        <SearchBar onSearch={searchReddit} />

        {loading && (
          <div className="loading">
            <p>Searching Reddit...</p>
          </div>
        )}

        {error && (
          <div className="error">
            <p>{error}</p>
          </div>
        )}

        {!loading && !error && searched && posts.length === 0 && (
          <div className="no-results">
            <p>No posts found. Try a different keyword.</p>
          </div>
        )}

        {!loading && !error && posts.length > 0 && (
          <PostList posts={posts} />
        )}
      </main>

      <footer className="app-footer">
        <div className="container">
          <p>&copy; {new Date().getFullYear()} Reddit Social Listening App</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
